    { title: 'Adobe', value: 19.99, color: 'rgba(28, 133, 255, 0.9)' },
    { title: 'Spotify', value: 4.99, color: 'rgba(255, 194, 59, 0.9)' },
    { title: 'Paste', value: 14.99, color: 'rgba(233, 166, 153, 0.9)'},
];


